package push

import "testing"

const ERR_SHOULD_NOT_BE_NIL = "err should not be nil"
const ERR_SHOULD_BE_NIL = "err should be nil"

func TestDecodeErr(t *testing.T) {
	toDecode := ""
	dataUtil := NewDataUtilsImpl()

	_, err := dataUtil.Decode(toDecode)
	if err == nil {
		t.Error(ERR_SHOULD_NOT_BE_NIL)
	}
}

func TestDecodeWrongString(t *testing.T) {
	toDecode := ""
	dataUtil := NewDataUtilsImpl()

	_, err := dataUtil.Decode(toDecode)
	if err == nil {
		t.Error(ERR_SHOULD_NOT_BE_NIL)
	}
}

func TestDecompressGzipWrongByte(t *testing.T) {
	toDecode := ""

	dataUtil := NewDataUtilsImpl()
	toDecompress, err := dataUtil.Decode(toDecode)
	if err != nil {
		t.Error(ERR_SHOULD_BE_NIL)
	}

	_, err = dataUtil.Decompress(toDecompress, 1)
	if err == nil {
		t.Error(ERR_SHOULD_NOT_BE_NIL)
	}
}

func TestDecompressZlibWrongByte(t *testing.T) {
	toDecode := ""

	dataUtil := NewDataUtilsImpl()
	toDecompress, err := dataUtil.Decode(toDecode)
	if err != nil {
		t.Error(ERR_SHOULD_BE_NIL)
	}

	_, err = dataUtil.Decompress(toDecompress, 2)
	if err == nil {
		t.Error(ERR_SHOULD_NOT_BE_NIL)
	}
}
